import{_ as s,o as a,c as n,O as e}from"./chunks/framework.3ac6d672.js";const A=JSON.parse('{"title":"读一读李清照的词","description":"","frontmatter":{},"headers":[],"relativePath":"读一读李清照的词.md","filePath":"读一读李清照的词.md"}'),p={name:"读一读李清照的词.md"},l=e(`<h1 id="读一读李清照的词" tabindex="-1">读一读李清照的词 <a class="header-anchor" href="#读一读李清照的词" aria-label="Permalink to &quot;读一读李清照的词&quot;">​</a></h1><p>上学的时候，那些古诗词是用来背的。长大以后，才算真正感同身受。</p><p>李清照，自不必过多介绍。千古才女，唯此一人。</p><p><strong>如梦令</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">常记溪亭日暮，沉醉不知归路。兴尽晚回舟，误入藕花深处。争渡，争渡，惊起一滩鸥鹭。</span></span></code></pre></div><p><strong>醉花阴</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">薄雾浓云愁永昼，瑞脑销金兽。佳节又重阳，玉枕纱厨，半夜凉初透。</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">东篱把酒黄昏后，有暗香盈袖。莫道不销魂，帘卷西风，人比黄花瘦。</span></span></code></pre></div><p><strong>声声慢</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">寻寻觅觅，冷冷清清，凄凄惨惨戚戚。乍暖还寒时候，最难将息。三杯两盏淡酒，怎敌他、晚来风急！雁过也，正伤心，却是旧时相识。</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">满地黄花堆积，憔悴损，如今有谁堪摘？守着窗儿，独自怎生得黑！梧桐更兼细雨，到黄昏、点点滴滴。这次第，怎一个愁字了得！</span></span></code></pre></div><p><strong>一剪梅</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">红藕香残玉簟秋。轻解罗裳，独上兰舟。云中谁寄锦书来？雁字回时，月满西楼。</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">花自飘零水自流。一种相思，两处闲愁。此情无计可消除，才下眉头，却上心头。</span></span></code></pre></div><p><strong>武陵春</strong></p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">风住尘香花已尽，日晚倦梳头。物是人非事事休，欲语泪先流。</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">闻说双溪春尚好，也拟泛轻舟。只恐双溪舴艋舟，载不动许多愁。</span></span></code></pre></div>`,13),t=[l];function o(c,i,r,d,C,_){return a(),n("div",null,t)}const h=s(p,[["render",o]]);export{A as __pageData,h as default};
